frappe.ui.form.on('Quotation', {
	company(frm) {
		// your code here
		frappe.call({
		    method:"company_based_naming.custom_methods.get_series_1",
		    args:{
		        "company":cur_frm.doc.company,
		        "doctype":cur_frm.doc.doctype   
		    },
		    callback: function(r){
		        console.log(r.message)
		        var series=[]
		        r.message.forEach(function(element){
		            console.log(element.naming_series_)
		            series.push(element.naming_series_)
		        })
		        if(series.length>0){
		        cur_frm.set_df_property('naming_series', 'options', series);
		        cur_frm.refresh_field('naming_series');

		        }
		    }
		})
	}
})

